// Replace *ƒ* with the ƒunction respective of your variable
var kkey={
		a:0,
		l:[0]
	},
	key={
		a:0,
		c:'',
		n:0
	};

/* Edit The Functions */
function press(_e_){
	if(key.a!=_e_.keyCode){
		key.n++;
	}
	key.a=_e_.keyCode;
	key.c=_e_.key;
	kkey.l[key.n]=key.a;
}
function release(_e_){
	key.a=0;
	key.c='';
	key.n--;
	kkey.l.splice(key.n, kkey.l.length-key.n-1);
	console.log("--------Reset--------");
}
/* Stop Here */
function keyUpdt(){
	kkey.a=kkey.l[kkey.l.length-1];
	if(key.n==0){
		kkey.a=0;
	}
	console.log(kkey.a);
}

window.onload=function(){
	document.onkeypress=press;
	document.onkeyup=release;
};